// To slice vi Redux Toolkit
import { createSlice } from '@reduxjs/toolkit';

const productsSlice = createSlice({
  name: 'products',
  initialState: {
    items: [],
    filter: {
      category: '',
      minPrice: '',
      maxPrice: '',
      inStock: false,
    },
    sort: {
      field: 'name',
      direction: 'asc',
    },
  },
  reducers: {
    // T ng to action creators v reducers
    addProduct: (state, action) => {
      state.items.push({ ...action.payload, id: Date.now() });
    },
    updateProduct: (state, action) => {
      const index = state.items.findIndex(item => item.id === action.payload.id);
      if (index !== -1) {
        state.items[index] = action.payload;
      }
    },
    deleteProduct: (state, action) => {
      state.items = state.items.filter(item => item.id !== action.payload);
    },
    setFilter: (state, action) => {
      state.filter = action.payload;
    },
    setSort: (state, action) => {
      state.sort = action.payload;
    },
  },
});

// Export actions v reducer
export const { addProduct, updateProduct, deleteProduct, setFilter, setSort } = productsSlice.actions;
export default productsSlice.reducer;

// To store
import { configureStore } from '@reduxjs/toolkit';
import productsReducer from './productsSlice';

export const store = configureStore({
  reducer: {
    products: productsReducer,
  },
});

